+impl<'a> From<&'a ruff_python_semantic::SubmoduleImport<'_>> for ImportInfo<'a> {
+    fn from(import: &'a ruff_python_semantic::SubmoduleImport) -> Self {
+        let module_name = import.module_name();
+        let member_name = import.member_name();
+        let qualified_name = import.qualified_name();
+        Self {
+            module_name,
+            member_name,
+            qualified_name,
+        }
+    }
+}
+
+/// Computes the range for the diagnostic to point at the exact private name segment.
+fn compute_private_name_range(
+    checker: &Checker,
+    binding: &ruff_python_semantic::Binding,
+    is_from_import: bool,
+    private_name: &str,
+    index: usize,
+) -> TextRange {
+    let Some(node_id) = binding.source else {
+        return binding.range();
+    };
+
+    let statement = checker.semantic().statement(node_id);
+
+    if is_from_import {
+        // For `from` imports
+        let Stmt::ImportFrom(StmtImportFrom { module, names, .. }) = statement else {
+            return binding.range();
+        };
+
+        let Some(alias) = names.iter().find(|alias| {
+            alias.asname.as_ref().map_or(&alias.name, |asname| asname).range() == binding.range()
+        }) else {
+            return binding.range();
+        };
+
+        // If the private name is the member being imported (last segment in qualified name)
+        // e.g., `from i import _j` where _j is the private name
+        if alias.name.as_str() == private_name {
+            // Point at the member name itself
+            return alias.name.range();
+        }
+
+        // If the private name is in the module path, we need to locate it in the module expression
+        // e.g., `from c._d import e` where _d is the private name
+        if let Some(module) = module {
+            if let Some(range) = find_private_segment_in_module_path(
+                checker.source(),
+                module.range(),
+                private_name,
+                index,
+            ) {
+                return range;
+            }
+        }
+
+        // Fall back to binding range
+        binding.range()
+    } else {
+        // For `import` statements, we need to locate the private segment in the module path
+        let Stmt::Import(StmtImport { names, .. }) = statement else {
+            return binding.range();
+        };
+
+        // Find the alias that corresponds to this binding
+        let Some(alias) = names.iter().find(|alias| {
+            alias.asname.as_ref().map_or(&alias.name, |asname| asname).range() == binding.range()
+        }) else {
+            return binding.range();
+        };
+
+        // Find the private segment in the module path
+        // e.g., `import _aaa` or `import bbb.ccc._ddd`
+        find_private_segment_in_module_path(
+            checker.source(),
+            alias.name.range(),
+            private_name,
+            index,
+        )
+        .unwrap_or_else(|| binding.range())
+    }
+}
+
+/// Finds the range of a private segment within a module path.
+fn find_private_segment_in_module_path(
+    source: &str,
+    range: TextRange,
+    private_name: &str,
+    index: usize,
+) -> Option<TextRange> {
+
+    // Tokenize the module path to find the private segment
+    let mut tokenizer = SimpleTokenizer::new(source, range);
+    let mut current_index = 0;
+
+    while let Some(token) = tokenizer.next() {
+        if token.kind() == SimpleTokenKind::Name {
+            let token_text = &source[token.range()];
+            if current_index == index && token_text == private_name {
+                return Some(token.range());
+            }
+
+            if token_text != "." {
+                current_index += 1;
+            }
+        }
+    }
+
+    None
+}
