+/// Compute the [`TextRange`] of the private name segment within an import statement.
+///
+/// For module-path private names (e.g., `_d` in `from c._d import e`), searches for the
+/// private segment within the module identifier or alias name. For imported member private
+/// names (e.g., `_j` in `from i import _j`), uses the alias name range directly.
+fn private_name_range(
+    stmt: Option<&Stmt>,
+    private_name: &str,
+    segment_index: usize,
+    checker: &Checker,
+) -> Option<TextRange> {
+    let stmt = stmt?;
+    match stmt {
+        Stmt::ImportFrom(import_from) => {
+            let module_segments = import_from
+                .module
+                .as_ref()
+                .map(|m| m.id.split('.').count())
+                .unwrap_or(0);
+
+            if segment_index < module_segments {
+                // Private name is in the module path (e.g., `from _a import b` or `from c._d import e`).
+                let module_id = import_from.module.as_ref()?;
+                find_segment_range(module_id.range(), private_name, checker)
+            } else {
+                // Private name is the imported member (e.g., `from i import _j`).
+                // Find the matching alias by name.
+                let alias = import_from.names.iter().find(|alias| {
+                    alias.name.id.as_str() == private_name
+                        || alias
+                            .name
+                            .id
+                            .split('.')
+                            .any(|segment| segment == private_name)
+                })?;
+                find_segment_range(alias.name.range(), private_name, checker)
+            }
+        }
+        Stmt::Import(import) => {
+            // For `import _aaa` or `import bbb.ccc._ddd as eee`.
+            for alias in &import.names {
+                if let Some(range) = find_segment_range(alias.name.range(), private_name, checker) {
+                    return Some(range);
+                }
+            }
+            None
+        }
+        _ => None,
+    }
+}
+
+/// Find the [`TextRange`] of a name segment within a dotted identifier range.
+///
+/// For example, given the range of `bbb.ccc._ddd` and `private_name = "_ddd"`,
+/// returns the range covering `_ddd`.
+fn find_segment_range(
+    source_range: TextRange,
+    private_name: &str,
+    checker: &Checker,
+) -> Option<TextRange> {
+    let source_text = checker.locator().slice(source_range);
+    let offset = source_text.find(private_name)?;
+    let start = source_range.start() + TextSize::try_from(offset).ok()?;
+    let end = start + TextSize::try_from(private_name.len()).ok()?;
+    Some(TextRange::new(start, end))
+}
